import React, {useState} from "react"
import { Charity, CharityCategory } from "./types";
import { getRandomCharity } from "./util/util";
import './CharityPage.css';

import ChoosingCard from "./subcomponents/ChoosingCard.tsx";


const CharityPage: React.FC = () => {
  const [result, setResult] = useState<null | Charity>(null) 

  const handleSelect = (name: CharityCategory) => {
    setResult(getRandomCharity(name))
  }

    return (
    <div className="container">
      <h1>Help local volunteers in Ukraine</h1>
      <h3>The day to day help in Ukraine is done by grassroot local volunteer organisations who don't get financed by big fonds or the foreign financial help. They need our help and -- and I'm sure you've heard it before, but it's true -- every little bit helps. The price of coffee buys food for a cat or meds for a lonely grandma. MOre good marketing descripttion.</h3>
      <h4>Select a category to get a random pick to donate to today!</h4>
      <div className="card-container">
        <ChoosingCard name={CharityCategory.pets} onClick={handleSelect}/>
        <ChoosingCard name={CharityCategory.children} onClick={handleSelect}/>
        <ChoosingCard name={CharityCategory.army} onClick={handleSelect}/>
        <ChoosingCard name={CharityCategory.humanitarian} onClick={handleSelect}/>
      </div>
      {result && (
        <div>
          <span>Today's donation</span>
          {result.name}
          {result.description}
          <a href={result.paypal}>Paypal</a>
        </div>
      )}
    </div>
    )
}

export default CharityPage
