import React, {useState, useRef, useEffect} from "react"

import './CharityPage.css';

import ChoosingCard from "./subcomponents/ChoosingCard.tsx";
import InfoModal from "./subcomponents/InfoModal.tsx";
import { Charity, CharityCategory } from "./types/index.ts";
import { getRandomCharity } from "./util/util.ts";

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    height: "50%"
  },
};

const CharityPage: React.FC = () => {
  const [result, setResult] = useState<null | Charity>(null) 
  const [modalIsOpen, setIsOpen] = useState<boolean>(false)

  const resultComp = useRef(null)

  const handleSelect = async (name: CharityCategory) => {
    await setResult(getRandomCharity(name, result?.name))
   resultComp?.current?.scrollIntoView({ behavior: 'smooth' })  
  }

  const toggleModal = () => {
    setIsOpen(!modalIsOpen)
  }

    return (
    <div className="parent-page">
      <div className="container">
        <InfoModal isOpen={modalIsOpen} onRequestClose={toggleModal} style={customStyles}/>
        <div className="title-background">
          <div className="title" dangerouslySetInnerHTML={{__html: 'Help <span style="font-weight: bold;">local volunteers</span> in Ukraine'}} />
        </div>
        <p className="description">
          Every day a wide variety of Ukrainian grassroots volunteers and established charity funds tirelessly help civilians, army, orphaned children and animals.
          They run purely on the donation basis and -- I know you've heard it before, but it's true! -- the price of a cup of coffee makes a difference (of course, if you want to donate more, you're very welcome üòâ)
        </p>
        <div className="content">
          <div className="selection">
            <h4 className="centered-text">Select a category to get a random pick to donate to today!</h4>
            <div className="card-container">
              <ChoosingCard name={CharityCategory.animals} onClick={handleSelect} selected={result?.category.includes(CharityCategory.animals)}/>
              <ChoosingCard name={CharityCategory.military} onClick={handleSelect} selected={result?.category.includes(CharityCategory.military)}/>
              <ChoosingCard name={CharityCategory.humanitarian} onClick={handleSelect} selected={result?.category.includes(CharityCategory.humanitarian)}/>
            </div>
          </div>
          {result && (
            <div className="result-container" ref={resultComp}>
              <h4>üíôüíõ Today's donation</h4>
              <span className="result-row">‚ÑπÔ∏è {result.name}</span>
              <span className="result-row">‚ùì {result.description}</span>
              <span className="result-row" >üí° <a href={result.link}> Learn more</a></span>
              {result.payment_link ? <span className="result-row" >üíµ <a href={result.payment_link}> Donate here</a></span> : <span className="result-row">{`üíµ Paypal: ${result.paypal_address}`}</span>}
              <div className="result-row link" onClick={() => handleSelect(result.category[0])}>Pick a new one</div>
            </div>
            )}
        </div>
        <div style={{marginTop: 'auto', paddingBottom: 16, display: 'flex', justifyContent: 'center', width: '100%'}} onClick={toggleModal}>
        <a href="https://twitter.com/share?ref_src=twsrc%5Etfw" className="twitter-share-button" data-size="large" data-text="I just donated to help local volunteers in Ukraine. Do the same at ukrainevolunteers.help" data-show-count="false">Tweet about your donation</a><script async src="https://platform.twitter.com/widgets.js" charSet="utf-8"></script>
          <span className="link" style={{marginLeft: 16}}>Read more about the project</span>
        </div>
      </div>
    </div>
    )
}

export default CharityPage
