import React, {useState, useRef} from "react"
import styled from 'styled-components'
import Flag from './assets/flag.png';

import './CharityPage.css';

import ChoosingCard from "./subcomponents/ChoosingCard";
import InfoModal from "./subcomponents/InfoModal";
import { Charity, CharityCategory } from "./types";
import { getRandomCharity } from "./util/util";


const CharityPage: React.FC = () => {
  const [result, setResult] = useState<null | Charity>(null) 
  const [modalIsOpen, setIsOpen] = useState<boolean>(false)

  const resultComp = useRef(null)

  const handleSelect = async (name: CharityCategory) => {
    await setResult(getRandomCharity(name, result?.name))
   resultComp?.current?.scrollIntoView({ behavior: 'smooth' })  
  }

  const toggleModal = () => {
    setIsOpen(!modalIsOpen)
  }

    return (
    <Parent>
      <Container>
        <InfoModal isOpen={modalIsOpen} onRequestClose={toggleModal}/>
        <Logo>
          <Title dangerouslySetInnerHTML={{__html: 'Help <span style="font-weight: bold;">local volunteers</span> in Ukraine'}} />
        </Logo>
        <p className="description">
          Every day a wide variety of Ukrainian grassroots volunteers and established charity funds tirelessly help civilians, army, children and animals.
          They run on donation basis and -- I know you've heard it before, but it's true! -- the price of a cup of coffee makes a difference (of course, if you want to donate more, you're very welcome 😉).
          All listed orgs are manually picked and provide reports on their spendings. 
        </p>
        <div className="content">
          <div style={{width: "100%"}}>
            <h4 className="centered-text">Choose where to donate</h4>
            <div className="card-container">
              <ChoosingCard name={CharityCategory.animals} onClick={handleSelect} selected={result?.category.includes(CharityCategory.animals)}/>
              <ChoosingCard name={CharityCategory.military} onClick={handleSelect} selected={result?.category.includes(CharityCategory.military)}/>
              <ChoosingCard name={CharityCategory.humanitarian} onClick={handleSelect} selected={result?.category.includes(CharityCategory.humanitarian)}/>
            </div>
          </div>
          {result && (
            <div className="result-container" ref={resultComp}>
              <h4 style={{margin: 0}}>💙💛 Today's donation</h4>
              <span className="result-row">ℹ️ {result.name}</span>
              <span className="result-row">❓ {result.description}</span>
              <span className="result-row" >💡 <a href={result.link}> Learn more</a></span>
              {result.payment_link ? <span className="result-row" >💵 <a href={result.payment_link}> Donate here</a></span> : <span className="result-row">{`💵 Paypal: ${result.paypal_address}`}</span>}
              <div className="result-row link" onClick={() => handleSelect(result.category[0])}>Pick a new one</div>
            </div>
            )}
        </div>
        <div style={{marginTop: 'auto', padding: 16, display: 'flex', justifyContent: 'center', width: '100%'}} onClick={toggleModal}>
        <a href="https://twitter.com/share?ref_src=twsrc%5Etfw" target='_blank' className="twitter-share-button" style={{color: 'black', textAlign: 'center'}} data-size="large" data-text="I just donated to help local volunteers in Ukraine. Do the same at ukrainevolunteers.help" data-show-count="false">Tweet about your donation</a><script async src="https://platform.twitter.com/widgets.js" charSet="utf-8"></script>
          <span className="link" style={{marginLeft: 16, textAlign: 'center'}}>Read more about the project</span>
        </div>
      </Container>
    </Parent>
    )
}

const Parent = styled.div`
  display: flex;
  justify-content: center;
  height: 100vh;
`

const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 50%;
  padding: 48px;
  padding-top: 0;

  @media (max-width: 768px) {
    width: 100%;
    padding: 32px;
    padding-top: 0;
  }
`

const Logo = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 32px;

  @media (min-width: 1440px) {
    padding: 48px;
  }

  &:after {
    content: "";
    background-image: url(${Flag});
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    opacity: 0.6;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    z-index: -1;   
`

const Title = styled.div`
  font-size: 32px;
  text-align: center;
  max-width: 200px;

  @media (max-width: 480px) {
    flex-direction: column;
  }
`

export default CharityPage
