import { CharityCategory } from "../types.ts"
import {charities} from "./data.ts"

export const getRandomCharity = (name: CharityCategory) => {
    const filteredCharities = charities.filter(charity => charity.category.includes(name))
    return filteredCharities[Math.floor(Math.random() * filteredCharities.length)]
}
